INSERT INTO `customs`.`gv_quest_catalog` (`id`, `reset_type`, `quest_type`, `creature_entry`, `creature_type`, `item_entry`, `quest_count`, `required_expansion`, `quest_name_cs`, `info_cs`, `quest_name_en`, `info_en`, `reward1`, `reward1_count`, `reward2`, `reward2_count`, `reward3`, `reward3_count`, `reward4`, `reward4_count`, `reward5`, `reward5_count`, `enabled`) VALUES
	(1, 'daily', 'kill_boss_normal', 0, '', 0, 50, '', '[Boss Normal] Očista Dungeonů', 'V hlubinách dungeonů povstávají mocní velitelé. Vstup do libovolného dungeonu a porážej jejich bossy, aby ses postavil sílícímu chaosu ohrožujícímu Azeroth.', '[Boss Normal] Purging the Dungeons', 'From the depths of countless dungeons, powerful warlords rise once more. Enter any dungeon and defeat its bosses to stand against the growing chaos threatening all of Azeroth.', 'random', 25, '', 0, '', 0, '', 0, '', 0, 1),
	(2, 'daily', 'kill_boss_heroic', 0, '', 0, 30, '', '[Heroic Boss] Vládci Podzemí', 'V hlubinách dungeonů povstávají mocní velitelé. Vstup do libovolného dungeonu a porážej jejich bossy, aby ses postavil sílícímu chaosu ohrožujícímu Azeroth.', '[Heroic Boss] Lords of the Underdepths', 'From the depths of countless dungeons, powerful warlords rise once more. Enter any dungeon and defeat its bosses to stand against the growing chaos threatening all of Azeroth.', 'random', 25, '', 0, '', 0, '', 0, '', 0, 1),
	(3, 'daily', 'kill_creature_type', 0, 'beast', 0, 200, '', '[Beast] Lov Zběsilých Bestií', 'Po celém Azerothu se toulají divoké bestie, které ohrožují cestovatele i obchodní trasy. Vydej se do krajiny, vyhledej tyto tvory a skol požadovaný počet, aby ses zasloužil o bezpečí svého lidu.', '[Beast] Hunt of the Wild Beasts', 'Across all of Azeroth, ferocious beasts roam freely, threatening travelers and trade routes alike. Venture into the wilderness, seek out these creatures, and slay the required number to restore safety to the land.', 'random', 25, '', 0, '', 0, '', 0, '', 0, 1),
	(4, 'daily', 'kill_creature_type', 0, 'undead', 0, 200, '', '[Undead] Lovec Nemrtvých', 'Stíny nemrtvých znovu kráčí světem a šíří zkázu všude, kam vstoupí. Vyhledej tyto zvrácené tvory a znič je, aby jejich temné síly dále neohrožovaly Azeroth.', '[Undead] Hunter of the Undead', 'The shadows of the undead once again walk the world, spreading ruin wherever they tread. Seek out these twisted beings and destroy them before their dark power threatens Azeroth any further.', 'random', 25, '', 0, '', 0, '', 0, '', 0, 1),
	(5, 'daily', 'kill_creature_type', 0, 'elemental', 0, 200, '', '[Elemental] Hněv Živlů', 'Síly živlů se vymkly kontrole a elementálové pustoší krajinu. Postav se těmto bytostem, znič je a obnov rovnováhu, než jejich divoká moc zasáhne celý Azeroth.', '[Elemental] Fury of the Elements', 'The forces of nature have broken free, and rampaging elementals ravage the land. Confront these beings, bring them down, and restore balance before their wild power engulfs all of Azeroth.', 'random', 25, '', 0, '', 0, '', 0, '', 0, 1),
	(6, 'daily', 'kill_creature_type', 0, 'demon', 0, 200, '', '[Demon] Očista Pekel', 'Démoni z hlubin Zkázonosných říší pronikají do světa a šíří chaos. Vyhledej tyto služebníky plamenů a poraž je, aby se jejich pekelný vliv nerozšířil napříč Azerothem.', '[Demon] Cleansing the Inferno', 'Demons from the depths of the Fel Realms breach into the world, spreading chaos in their wake. Hunt down these servants of flame and defeat them before their hellish influence spreads across Azeroth.', 'random', 25, '', 0, '', 0, '', 0, '', 0, 1),
	(7, 'daily', 'kill_creature_type', 0, 'dragonkin', 0, 200, '', '[Dragonkin] Hněv Dračí Krve', 'Dračí služebníci se probouzí a hlídají území svých prastarých pánů. Postav se těmto mocným tvorům, porážej je a oslab jejich vliv dříve, než znovu získají sílu ovládnout svět.', '[Dragonkin] Wrath of Dragonblood', 'The dragonkin awaken to guard the domains of their ancient masters. Confront these powerful beings, defeat them, and weaken their influence before they regain the strength to dominate the world once more.', 'random', 25, '', 0, '', 0, '', 0, '', 0, 1),
	(8, 'daily', 'kill_creature_type', 0, 'giant', 0, 200, '', '[Giant] Pád Obrů', 'Kolosální obři se zvedají ze svých úkrytů a ohrožují celý kraj. Vyhledej tyto titánské tvory, poraz je a zastav jejich ničivou sílu dříve, než rozdrtí vše, co jim stojí v cestě.', '[Giant] Fall of the Giants', 'Colossal giants rise from their hidden lairs, threatening entire regions. Seek out these towering creatures, bring them down, and halt their destructive might before they crush all that stands in their way.', 'random', 25, '', 0, '', 0, '', 0, '', 0, 1),
	(9, 'daily', 'kill_creature_type', 0, 'humanoid', 0, 200, '', '[Humanoid] Hrozba Smrtelníků', 'Po Azerothu se potulují znepřátelené humanoidní kmeny a bandité, kteří napadají každého, kdo projde jejich územím. Vyhledej tyto nepřátele a porážej je, aby byl svět opět o něco bezpečnější.', '[Humanoid] Threat of the Mortals', 'Hostile humanoid tribes and bandits roam across Azeroth, attacking all who cross their path. Hunt these foes and defeat them to make the world a safer place once again.', 'random', 25, '', 0, '', 0, '', 0, '', 0, 1),
	(10, 'daily', 'pvp_kill', 0, '', 0, 100, 'bg_arena', '[PvP] Krev na Bojišti', 'Válka mezi frakcemi nikdy neutichá. Vydej se na bojiště i do divočiny, vyhledej hráče nepřátelské frakce a porážej je, abys dokázal svou sílu a posílil postavení své aliance nebo hordy.', '[PvP] Blood on the Battlefield', 'The war between the factions never ceases. Head into battlegrounds or the wilds, find players of the opposing faction, and defeat them to prove your strength and bolster the standing of your own.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(11, 'daily', 'loot_herbal', 0, '', 0, 300, 'prof_herbalism', '[Herbalism] Dech Divoké Přírody', 'Bylinky divoké přírody skrývají mocné esence. Putuj krajinou, vyhledávej rostliny určené pro herbalisty a skliď je, aby jejich síla mohla být využita ve službách tvé guildy.', '[Herbalism] Breath of the Wilds', 'Herbs of the untamed wilds hold powerful essences. Travel the land, seek out herbalist plants, and gather them so their strength can be used in future endeavors.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(12, 'daily', 'loot_ores', 0, '', 0, 300, 'prof_mining', '[Mining] Hlubiny Živlů', 'Cenné rudy ukryté v žilách Azerothu čekají na zpracování. Putujte krajinou, vyhledávejte ložiska určená pro horníky a těžte je, aby vaše guilda získala suroviny potřebné pro své další výpravy.', '[Mining] Depths of the Elements', 'Precious ores lie hidden in Azeroth’s veins. Travel the land, locate mining deposits, and extract their resources to support your coming ventures.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(13, 'daily', 'loot_skinning', 0, '', 0, 300, 'prof_skinning', '[Skinning] Zrno z Kůže', 'Divoká zvěř po celém Azerothu skrývá cenné kožešiny a suroviny. Lovte bestie, stahujte je z kůže a sbírejte materiály potřebné pro výrobu zbrojí a dalších užitečných předmětů.', '[Skinning] Harvest of the Hide', 'Wild beasts across Azeroth carry valuable hides and materials. Hunt these creatures, skin them, and gather the resources needed for crafting armor and useful gear.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(14, 'daily', 'loot_fish', 0, '', 0, 300, 'prof_fishing', '[Fishing] Volání Hloubek', 'Vody Azerothu ukrývají vzácné úlovky a prastaré tvory. Vyrazte k jezerům, řekám i mořím, nahazujte své udice a lovte ryby, jejichž síla a hodnota mohou výrazně pomoci vašim dalším výpravám.', '[Fishing] Call of the Depths', 'The waters of Azeroth conceal rare catches and ancient creatures. Travel to lakes, rivers, and seas, cast your lines, and fish for what may aid you on future journeys.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(15, 'daily', 'craft_alchemy', 0, '', 0, 300, 'prof_alchemy', '[Alchemy] Síla Destilace', 'Esence světa lze přetavit v mocné elixíry. Vyrábějte lektvary, elixíry či další alchymistické směsi a uvolňujte sílu skrytou v ingrediencích, které mění průběh každé bitvy.', '[Alchemy] Power of Distillation', 'The world’s essences can be shaped into potent elixirs. Craft potions, elixirs, or any alchemical mixture and unleash the strength hidden within their ingredients.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(16, 'daily', 'craft_blacksmithing', 0, '', 0, 300, 'prof_blacksmithing', '[Blacksmithing] Oheň Kovárny', 'Plameny kovárny čekají na nové dílo. Kujte zbraně, zbroje či jakékoliv další předměty kovářského řemesla a posilujte tak moc, která vzniká z ohně, kovu a pevné ruky mistra.', '[Blacksmithing] Forgefire', 'The flames of the forge await new creations. Shape weapons, armor, or any other blacksmithing work to harness the strength born of fire, metal, and a steady hand.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(17, 'daily', 'craft_leatherworking', 0, '', 0, 300, 'prof_leatherworking', '[Leatherworking] Umění Kožešin', 'Zpracovávejte kožešiny a kůže získané z divokých tvorů a vytvářejte jakékoliv předměty kožedělného řemesla. Každý nový kus posiluje vaše dovednosti i připravenost na budoucí výzvy.', '[Leatherworking] Art of the Wild Leather', 'Process hides and leathers taken from wild creatures and craft any leatherworking goods. Each new creation sharpens your skills and readiness for what lies ahead.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(18, 'daily', 'craft_inscription', 0, '', 0, 300, 'prof_inscription', '[Inscription] Tajemství Inkoustu', 'Moc run a inkoustů dokáže měnit osudy hrdinů. Vyrábějte jakékoliv předměty písařského umění, jako jsou svitky, glyfy či pečetě, a uvolňujte sílu ukrytou v pradávných znacích.', '[Inscription] Secrets of the Ink', 'Runes and inks carry the power to shape destiny. Craft any inscription goods such as scrolls, glyphs, or seals and release the strength hidden in ancient symbols.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(19, 'daily', 'craft_tailoring', 0, '', 0, 300, 'prof_tailoring', '[Tailoring] Vlákna Moci', 'Tkaniny nasají sílu magie i řemesla. Šijte jakékoliv předměty krejčovského umění, například roucha, tašky či další látkové výrobky, a posilujte své dovednosti s každým novým stehem.', '[Tailoring] Threads of Power', 'Cloth absorbs the strength of magic and craft. Sew any tailoring goods, from robes to bags and other woven creations, and enhance your skills with each new stitch.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(20, 'daily', 'craft_jewelcrafting', 0, '', 0, 300, 'prof_jewelcrafting', '[Jewelcrafting] Lesk Drahokamů', 'Drahokamy ukrývají sílu země i magie. Obrušujte, vyřezávejte a vyrábějte jakékoliv předměty klenotnického umění, aby jejich třpyt posílil výbavu i sílu vašich budoucích hrdinských výprav.', '[Jewelcrafting] Gleam of the Gemstones', 'Gemstones hold the strength of earth and magic. Cut, carve, and craft any jewelcrafting items so their brilliance may empower your future ventures.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(21, 'daily', 'craft_engineering', 0, '', 0, 300, 'prof_engineering', '[Engineering] Jiskry Vynalézavosti', 'Ozubená kola a jiskřící metal převádějí chaos v sílu. Vyrábějte jakékoliv předměty inženýrského řemesla, ať už gadgety, výbušniny nebo přístroje, a posilujte svou technickou zručnost s každým novým vynálezem.', '[Engineering] Sparks of Ingenuity', 'Gears and charged metal turn chaos into power. Create any engineering items, whether gadgets, explosives, or devices, and hone your technical mastery with every invention.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(22, 'daily', 'craft_cooking', 0, '', 0, 300, 'prof_cooking', '[Cooking] Oheň Kuchyně', 'Vůně ohně a koření provází každé dobrodružství. Připravujte jakékoliv pokrmy kuchařského umění, od jednoduchých jídel po bohaté hostiny, a posilujte tak sílu i odhodlání svých spolubojovníků.', '[Cooking] Fire of the Hearth', 'The scent of fire and spices follows every hero. Prepare any culinary dishes, from simple meals to feast-worthy plates, to strengthen morale and endurance.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(23, 'daily', 'enchant', 0, '', 0, 300, 'prof_enchanting', '[Enchanting] Kouzla v Jádru Hmoty', 'Magie prostupuje každý předmět. Rozpouštějte vybavení na esence, vytvářejte jakékoliv očarování a vkládejte do zbraní i zbrojí sílu, která dokáže změnit výsledek každého střetu.', '[Enchanting] Magic at the Core of Matter', 'Magic permeates every object. Break down gear for essences, craft any enchantments, and infuse weapons and armor with power that can decide the outcome of battle.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(24, 'daily', 'disenchant', 0, '', 0, 300, 'prof_enchanting', '[Disenchant] Esence Zničené Moci', 'Staré zbraně a zbroje ukrývají zbytky dávné magie. Rozpouštějte vybavení na prach, esence a střepy moci a sbírejte tak suroviny potřebné pro další očarování a magické úpravy.', '[Disenchant] Essence of Broken Power', 'Old weapons and armor retain traces of ancient magic. Break down gear into dust, essences, and shards and gather the materials needed for future enchantments and arcane work.', 'random', 50, '', 0, '', 0, '', 0, '', 0, 1),
	(25, 'daily', 'craft_item', 0, '', 40211, 200, 'prof_alchemy', '[Craft] Elixír Rychlosti', 'Vyrobte vzácný Potion of Speed, jehož esence dokáže naplnit tělo hbitostí a mocí. Sesbírejte potřebné ingredience, aktivujte své alchymistické dovednosti a vyrobte tuto drahocennou dávku rychlosti.', '[Craft] Elixir of Speed', 'Craft the rare Potion of Speed, a brew whose essence fills the body with agility and power. Gather the required ingredients, draw on your alchemical skill, and create this precious dose of swiftness.', 'random', 75, '', 0, '', 0, '', 0, '', 0, 1),
	(26, 'daily', 'craft_item', 0, '', 40212, 200, 'prof_alchemy', '[Craft] Elixír Divoké Magie', 'Vyrobte Potion of Wild Magic a probuďte nevyzpytatelné proudy magie. Smíchejte vzácné ingredience a vytvořte lektvar, jehož moc posílí kouzla i nečekané výbuchy energie.', '[Craft] Potion of Wild Magic', 'Craft a Potion of Wild Magic and unleash unpredictable arcane currents. Combine rare ingredients to create a brew that empowers spells with volatile bursts of energy.', 'random', 75, '', 0, '', 0, '', 0, '', 0, 1),
	(27, 'daily', 'craft_item', 0, '', 40093, 200, 'prof_alchemy', '[Craft] Indestructible Potion', 'Vyrobte Indestructible Potion, lektvar zvyšující vytrvalost a odolnost. Smíchejte pevné esence země a vytvořte nápoj, který posílí tělo proti drtivým úderům.', '[Craft] Indestructible Potion', 'Craft a Indestructible Potion, a brew that grants heightened endurance and resilience. Combine sturdy earth essences to craft a tonic that strengthens the body against crushing blows.', 'random', 75, '', 0, '', 0, '', 0, '', 0, 1),
	(28, 'daily', 'craft_item', 0, '', 33448, 200, 'prof_alchemy', '[Craft] Runový Elixír Many', 'Vyrobte Runic Mana Potion, naplněný magickými runami. Spojte čistou esenci many a runové symboly, abyste vytvořili lektvar obnovující velké množství magické síly.', '[Craft] Runic Mana Potion', 'Craft a Runic Mana Potion infused with magical runes. Combine pure mana essence with runic symbols to craft a potion that restores a significant amount of magical power.', 'random', 75, '', 0, '', 0, '', 0, '', 0, 1),
	(29, 'daily', 'craft_item', 0, '', 33447, 200, 'prof_alchemy', '[Craft] Runový Elixír Léčení', 'Vyrobte Runic Healing Potion spojením léčivých esencí a runové magie. Tento mocný lektvar rychle zacelí rány a vrátí ztracenou životní sílu.', '[Craft] Runic Healing Potion', 'Craft a Runic Healing Potion by merging restorative essences with runic magic. This potent brew rapidly mends wounds and restores lost vitality.', 'random', 75, '', 0, '', 0, '', 0, '', 0, 1),
